import React, {Component} from 'react';
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
  } from "react-device-detect";

import TopHeader from '../component/top_header.js';
import InstituteHeader from '../component/institute_header.js';
import NavMenu from '../component/nav_header.js';
import MobileHeader from '../component/mobile_header.js';
import Banner from '../component/banner.js';
import IntroSec from '../component/intro_sec.js';
import CornorSec from '../component/cornor_sec.js';
import ActivitiesSec from '../component/activities_sec.js';
import InnovateSec from '../component/innovate_sec.js';
import LanguageSec from '../component/language_sec.js';
import BharatDarshan from '../component/bharat_darshan.js';
import QuizSec from '../component/quiz_sec.js';
import StorySec from '../component/story_sec.js';
import GallerySec from '../component/gallery_sec.js';
import FooterSec from '../component/footer_sec.js';




class Home extends Component {
    componentWillMount() {
        var scrollpos = window.scrollY;
        var header_sec = document.querySelector("custom-section");

        function add_class_on_scroll() {
            header_sec.classList.add("header_sec--active");
        }

        function remove_class_on_scroll() {
            header_sec.classList.remove("header_sec--active");
        }

        window.addEventListener('scroll', function(){ 
            //Here you forgot to update the value
            scrollpos = window.scrollY;

            if(scrollpos > 10){
                add_class_on_scroll();
            }
            else {
                remove_class_on_scroll();
            }
        });
    
    }
    render(){
        
        return(
            <div>
                <BrowserView>
                    <div className="header_sec">
                        <TopHeader/>
                        <InstituteHeader/>
                        <NavMenu/>
                    </div>
               </BrowserView>
               <MobileView>
                    <MobileHeader/>
               </MobileView>
               <Banner/>
               <IntroSec/>
               <CornorSec/>
               <ActivitiesSec/>
               <InnovateSec/>
               <LanguageSec/>
               <BharatDarshan/>
               <QuizSec/>
               <StorySec/>
               <GallerySec/>
               <FooterSec/>
            </div>
        );

        
    }

}
export default  Home;
