import { ListGroup, ListGroupItem, } from 'reactstrap';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import AOS from 'aos';
import 'aos/dist/aos.css';







class TestimonialsSec  extends Component {


    render(){
        return(
            <div className="testimonials_sec">
                
                <div className="container testimonials_wrapper" style={{background:`url('/images/testimonial_wrapper_bg.svg')`,}}>

                    <div className="testimonials_patten_icon">
                        <img className="t_circle_one" src="images/t_circle_one.svg" alt="t circle one"/>
                        <img className="t_circle_two" src="images/t_circle_two.svg" alt="t circle one"/>
                    </div>
                    <h3 className="blog_title text-uppercase font-weight-bold text-center pb-3" data-aos="fade-up">i-BHARAT TESTIMONIALS</h3>
                    <div className="testimonials_contain">
                        <div className="testimonials_slider">
                            <div className="testimonials_photo">
                                <img src="images/user_photo.png" alt="User Photo"/>
                            </div>
                            <div className="text_content">
                                <p> different scholars. Herbert Spencer believed in the 'complete-living aimâ€™ which signifies that education should prepare the children for life. Rousseau and Mahatma Gandhi also supported the view as they believed in the complete development or perfection of nature. Education provides a progressive development of innate abilities that creates good citizens.</p>
                            </div>
                        </div>
                    </div>
                    <div className="testimonials_circle">
                        <div className="circle circle_one">&nbsp;</div>
                        <div className="circle circle_two">&nbsp;</div>
                    </div>
                 </div>
            </div>

            
        );

        
    }

}
export default  TestimonialsSec;
