import React, {Component} from 'react';

import { BrowserRouter, Route, Link } from "react-router-dom";
import { Button } from 'reactstrap';
import { Nav, NavItem, NavLink } from 'reactstrap';
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";


import BannerPhotoOne from '../images/banner_photo_one.png';

class Banner extends Component {


    render(){
        var settings = {
            dots: true,
            infinite: true,
            speed: 500,
            slidesToShow: 1,
            slidesToScroll: 1,
            button: false,
          };
        return(
            <div className="banner">
               <Slider {...settings}>
                    <div>
                    <div style={{background:`url(${BannerPhotoOne})`,}}>
                        <div className="text_content">
                            <h5 className="text-white">Welcome to i-ADMISSION</h5>
                            <h3 className="banner_title text-white">We Ensure <span>better Education</span> for a better World</h3>
                            <BrowserRouter>
                                <Link class="more_btn" to="#">Learn More</Link>
                            </BrowserRouter>
                        </div>
                    </div>
                    </div>
                    <div>
                        <h3>2</h3>
                    </div>
                    <div>
                        <h3>3</h3>
                    </div>
                    <div>
                        <h3>4</h3>
                    </div>
                    <div>
                        <h3>5</h3>
                    </div>
                    <div>
                        <h3>6</h3>
                    </div>
                </Slider>
            </div>

            
        );

        
    }

}
export default  Banner;
