import React, {Component} from 'react';
import { ListGroup, ListGroupItem, } from 'reactstrap';
import { BrowserRouter, Route, Link } from "react-router-dom";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import AOS from 'aos';
import 'aos/dist/aos.css';







class BriefDescription  extends Component {


    render(){
        return(
            <div className="brief_description">
                <div className="container">
                    <h3 className="blog_title text-uppercase font-weight-bold text-center pb-3" data-aos="fade-up">BRIEF DESCRIPTION i-BHARAT  </h3>
                    <div className="brief_contain d-flex">
                        <div className="brief_photo">
                            <img src='images/brief_photo.svg' alt="Brief Photo"/>
                        </div>
                        <div className="text_contain">
                            <p className="description">Education", says Aristotle, “is the creation of a sound mind in a sound body". It encompasses in itself the all-round development of an individual. The aim of education has been categorized variously by different scholars. Herbert Spencer believed in the 'complete-living aim’ which signifies that education should prepare the children for life. Rousseau and Mahatma Gandhi also supported the view as they believed in the complete development or perfection of nature. Education provides a progressive development of innate abilities that creates good citizens. </p>
                            <p className="description">It also inculcates certain values and principles that prepare the children for their future. All round development has been considered as the first and foremost aim of education. The true goal of education transcends much beyond just awarding degrees to students. Education aims to develop the intellect of the learners. Education is not just a tool to earn money to meet the ends; it is the way to liberate the mind and soul of a person. The main imperative of NAVACHAR Technocrats is to produce a rational mind which works towards building an intellectual society.</p>
                            <BrowserRouter>
                            <Link className="more_btn" to="#">Read More</Link>
                            </BrowserRouter>
                        </div>
                    </div>

                   
                 </div>
            </div>

            
        );

        
    }

}
export default  BriefDescription;
