import React, {Component} from 'react';
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
  } from "react-device-detect";

import TopHeader from '../component/top_header.js';
import InstituteHeader from '../component/institute_header.js';
import NavMenu from '../component/nav_header.js';
import MobileHeader from '../component/mobile_header.js';
import Banner from '../component/banner.js';
import IntroSec from '../component/intro_sec.js';
import CornorSec from '../component/cornor_sec.js';
import ActivitiesSec from '../component/activities_sec.js';
import InnovateSec from '../component/innovate_sec.js';
import LanguageSec from '../component/language_sec.js';
import BharatDarshan from '../component/bharat_darshan.js';
import QuizSec from '../component/quiz_sec.js';
import StorySec from '../component/story_sec.js';
import GallerySec from '../component/gallery_sec.js';
import FooterSec from '../component/footer_sec.js';




class Home extends Component {

    constructor(props) {
        super(props)
        this.myRef = React.createRef()
        this.state = {scrollTop: 0}
      }
      
      onScroll = () => {
        const scrollY = window.scrollY //Don't get confused by what's scrolling - It's not the window
        const scrollTop = this.myRef.current.scrollTop
        console.log(`onScroll, window.scrollY: ${scrollY} myRef.scrollTop: ${scrollTop}`)
        this.setState({
          scrollTop: scrollTop
        })
      }
    
    render(){
        const {
            scrollTop
          } = this.state
        return(
            <div            >
                <BrowserView>
                    <div className="header_sec" ref={this.myRef}
        onScroll={this.onScroll}
        style={{
          border: '1px solid black',
          width: '600px',
          height: '100px',
          overflow: 'scroll',
        }} >
                        <TopHeader/>
                        <InstituteHeader/>
                        <NavMenu/>
                    </div>
               </BrowserView>
               <MobileView>
                    <MobileHeader/>
               </MobileView>
               <Banner/>
               <IntroSec/>
               <CornorSec/>
               <ActivitiesSec/>
               <InnovateSec/>
               <LanguageSec/>
               <BharatDarshan/>
               <QuizSec/>
               <StorySec/>
               <GallerySec/>
               <FooterSec/>
            </div>
        );

        
    }

}
export default  Home;
