import React, {Component} from 'react';
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
  } from "react-device-detect";


  import { BrowserRouter, Link } from "react-router-dom";

import TopHeader from '../component/top_header.js';
import NavMenu from '../component/nav_header.js';
import Banner from '../component/banner.js';
import BuildingConstruction from '../component/building_construction.js';
import ConstructionService from '../component/construction_service.js';
import ConstructionWork from '../component/construction_work.js';
import CivilElectrical from '../component/civil_electrical.js';
import HouseContractor from '../component/house_contractor.js';
import FaqSec from '../component/faq_sec.js';
import WorkingCities from '../component/working_cities.js';
import NewsLetter from '../component/news_letter.js';

import FooterSec from '../component/footer_sec.js';




class HomePage extends Component {

    /* On Scroll Js */
    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
      }
    
      componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
      }
    
      handleScroll = () => {
        if (window.scrollY > 10) {
          document.querySelector(".thikdr_page").className = "thikdr_page sticky"
        } else {
          document.querySelector(".thikdr_page").className = "thikdr_page";
        }
      };

      


      /* Toggle Js */
    constructor(props) {
        super();

        this.state = {
            thikdr_page : true,
        };
    }
    toggle() {
        this.setState({ thikdr_page: !this.state.thikdr_page});
    }



    render(){
        
        return(
            <div className={this.state.thikdr_page ? "thikdr_page" : "thikdr_page  active"}   onClick={this.toggle.bind(this)} onScroll={ this.handleScroll } >
                <div className="header_sec" onScroll={ this.handleScroll }>
                    <TopHeader/>
                    <div className="nave_header">

                        <div className="container">
                            <BrowserRouter>
                                <Link className="thikdr_logo" to="#">
                                    <img src="images/thikdr_logo.png" alt="Logo"/>
                                </Link>
                            </BrowserRouter>
                        
                            <NavMenu/>

                            <BrowserRouter>
                                <Link className="toggle_btn" to="#">
                                    <span>&nbsp;</span>
                                    <span>&nbsp;</span>
                                    <span>&nbsp;</span>
                                
                                </Link>
                            </BrowserRouter>
                        </div>
                    </div>
                </div>
              
               <Banner/>
               <BuildingConstruction/>
               <ConstructionService/>
               <ConstructionWork/>
               <CivilElectrical/>
               <HouseContractor/>
               <FaqSec/>
               <WorkingCities/>
               <NewsLetter/>
               <FooterSec/> 
            </div>
        );

        
    }

}
export default  HomePage;
