import React, {Component} from 'react';
import { ListGroup, ListGroupItem, Badge } from 'reactstrap';

import TopHeader from './top_header.js';
import InstituteHeader from './institute_header.js';
import NavMenu from './nav_header.js';


class HeaderSec extends Component {

    constructor(props) {
        super(props)
        this.state = { scrollClass: 'HeaderSec' }
      }
    
      handleScroll(e) {
        this.setState({ scrollClass: 'HeaderSec scrolling' })
        setTimeout(() => { this.setState({ scrollCLass: 'HeaderSec' }) }, 100)
      }
    render(){
        return(
            <div className={ HeaderSec } onScroll={ this.handleScroll }>
               <TopHeader/>
               <InstituteHeader/>
               <NavMenu/>
            </div>

            
        );

        
    }

}
export default  HeaderSec;
