import React, {Component} from 'react';
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
  } from "react-device-detect";


  import {
    BrowserRouter as Router,
    Link
  } from "react-router-dom";


import HeaderSec from '../component/header_sec.js';
import NavMenu from '../component/nav_header.js';
import Banner from '../component/banner.js';
import IBharatDashboardSec from '../component/i_bharat_dashboard_sec.js';
import BriefDescription from '../component/brief_description.js';
import IBharatService from '../component/i_bharat_service.js';
import TestimonialsSec from '../component/testimonials_sec.js';
import ContactSec from '../component/contact_sec.js';
import FooterSec from '../component/footer_sec.js';


import IntroSec from '../component/intro_sec.js';
import CornorSec from '../component/cornor_sec.js';
import InnovateSec from '../component/innovate_sec.js';
import LanguageSec from '../component/language_sec.js';
import BharatDarshan from '../component/bharat_darshan.js';
import QuizSec from '../component/quiz_sec.js';
import StorySec from '../component/story_sec.js';
import GallerySec from '../component/gallery_sec.js';





class Home extends Component {
    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
      }
    
      componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
      }
    
      handleScroll = () => {
        if (window.scrollY > 10) {
          document.querySelector(".header_sec").className = "header_sec sticky"
        } else {
          document.querySelector(".header_sec").className = "header_sec";
        }
      };

    render(){
        
        return(
            <div className="i-bharat_page" style={{background:`url('/images/i-bharat_bg.svg')`,}}>
                <div className="header_sec" onScroll={ this.handleScroll }>
                    <BrowserRouter>
                        <Link className="i_bharat_logo" to="#">
                            <img src="images/i-bharat-logo.png" alt="i bharat logo"/>
                        </Link>
                    </BrowserRouter>
                   
                    <NavMenu/>
                </div>
              
               <Banner/>
               <IBharatDashboardSec/>
               <BriefDescription/>
               <IBharatService/>
               <TestimonialsSec/>
               <ContactSec/>
               <FooterSec/>

            </div>
        );

        
    }

}
export default  Home;
