import React, {Component} from 'react';
import {
    BrowserView,
    MobileView,
    isBrowser,
    isMobile
  } from "react-device-detect";

  import { ListGroup, ListGroupItem } from 'reactstrap';

  import { BrowserRouter, Link } from "react-router-dom";

import NavMenu from '../component/nav_header.js';
import Banner from '../component/banner.js';
// import IBharatDashboardSec from '../component/i_bharat_dashboard_sec.js';
// import BriefDescription from '../component/brief_description.js';
// import IBharatService from '../component/i_bharat_service.js';
// import TestimonialsSec from '../component/testimonials_sec.js';
// import ContactSec from '../component/contact_sec.js';
// import FooterSec from '../component/footer_sec.js';






class Home extends Component {

    /* On Scroll Js */
    componentDidMount() {
        window.addEventListener("scroll", this.handleScroll);
      }
    
      componentWillUnmount() {
        window.removeEventListener("scroll", this.handleScroll);
      }
    
      handleScroll = () => {
        if (window.scrollY > 10) {
          document.querySelector(".header_sec").className = "header_sec sticky"
        } else {
          document.querySelector(".header_sec").className = "header_sec";
        }
      };


      /* Toggle Js */
    constructor(props) {
        super();

        this.state = {
            thikdr_page : true,
        };
    }
    toggle() {
        this.setState({ thikdr_page: !this.state.thikdr_page});
    }



    render(){
        
        return(
            <div className={this.state.thikdr_page ? "thikdr_page" : "thikdr_page  active"}   onClick={this.toggle.bind(this)} style={{background:`url('/images/i-bharat_bg.svg')`,}}>
                <div className="header_sec" onScroll={ this.handleScroll }>
                    <div className="top_header">
                        <div className="container">
                            <div className="top_header">
                                <div className="contact_sec">
                                    <ListGroup>
                                        <ListGroupItem>
                                            <Link to="#">
                                                <img src="images/envelop_icon.svg" alt="Logo"/> info@example.com
                                            </Link>
                                        </ListGroupItem>
                                        <ListGroupItem>
                                            <Link to="#">
                                                <img src="images/phone_icon.svg" alt="Logo"/> +1-2345-6789-101
                                            </Link>
                                        </ListGroupItem>
                                        <ListGroupItem>Dapibus ac facilisis in</ListGroupItem>
                                    </ListGroup>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="nave_header">

                        <div className="container">
                            <BrowserRouter>
                                <Link className="thikdr_logo" to="#">
                                    <img src="images/thikdr_logo.png" alt="Logo"/>
                                </Link>
                            </BrowserRouter>
                        
                            <NavMenu/>

                            <BrowserRouter>
                                <Link className="toggle_btn" to="#">
                                    <span>&nbsp;</span>
                                    <span>&nbsp;</span>
                                    <span>&nbsp;</span>
                                
                                </Link>
                            </BrowserRouter>
                        </div>
                    </div>
                </div>
              
               <Banner/>
               {/* <IBharatDashboardSec/>
               <BriefDescription/>
               <IBharatService/>
               <TestimonialsSec/>
               <ContactSec/>
               <FooterSec/> */}

            </div>
        );

        
    }

}
export default  Home;
